function validateCurrenciesInput( $input ){

    $currenciesJson = getCurrenciesList();
    $currencies = [];

    foreach( $input as $currencie ){

        if( isset($currencie['currencie_code']) ){

            if( isset($currenciesJson[ $currencie['currencie_code']  ]) ){
                $currencies[ $currencie['currencie_code'] ] = [
                    'currencie_code' => $currencie['currencie_code'],
                    'symbol' => $currencie['symbol'] ?? $currenciesJson[$currencie['currencie_code']]['symbol'],
                    'number_of_decimals' => $currencie['number_of_decimals'] ??  2,
                    'rate' => $currencie['rate'] ?? '1.0000',
                ];
            }

        }

    }

    if( empty($currencies) ){

        $currencies = [
            'USD'=>[
                'currencie_code' => 'USD',
                'symbol' => '$',
                'number_of_decimals' => 2,
                'rate' => '1.0000',
            ]
        ];
    }

    return $currencies;

}